import styled from "styled-components";
import Menu from "../../components/Menu";
// import { Body } from "../../components/Text";
import { Link } from "../../routes";
const Header = styled.div`
  background: grey;
  display: flex;
  height: 50px;
  justify-content: space-between;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 100;
  width: 100%;
`;

const Logo = styled.a`
  background: black;
  flex: 0 0 auto;
  line-height: 50px;
  display: block;
  font-weight: bold;
  padding: 0 20px;
  text-decoration: none;
  border-right: 10px solid yellow;
  color: white;
  font-family: Times New Roman;
  letter-spacing: 1px;
  font-weight: 600;
  :visited {
    color: white;
  }
  :active {
    color: white;
  }
  :hover {
    color: yellow;
  }
`;

const ContentContainer = styled.div`
  min-height: 100%;
  background: yellow;
`;

